package condition;

public class IfElseCondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		if(10==15) {
			System.out.println("10 is equal  15");
		}else if(10>15){
			System.out.println("10 is greater then 15");
		}
		
		else {
			System.out.println("no print");
		}
	}

}
